import axios from "axios";
import { useState } from "react";
import WeatherInfo from "./WeatherInfo";

export default function Weather(props) {
  const [city, setCity] = useState(props.defaultCity);
  const [weatherData, setWeatherData] = useState({ ready: false });
  function handleResponse(response) {
    console.log(response.data);
    setWeatherData({
      ready: true,
      date: new Date(response.data.time * 1000),
      temperature: response.data.temperature.current,
      humidity: response.data.temperature.humidity,
      wind: response.data.wind.speed,
      description: response.data.condition.description,
      city: response.data.city,
      icon: response.data.condition.icon_url,
    });
  }
  function search() {
    let apiKey = "b6dboa0a631739a3fcfd0t3ff674a04b";
    let apiUrl = `https://api.shecodes.io/weather/v1/current?query=${city}&key=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
  }
  function handleSubmit(event) {
    event.preventDefault();
    search();
  }
  function handleChange(event) {
    setCity(event.target.value);
  }
  if (weatherData.ready) {
    return (
      <div className="container">
        <div className="weather-app-wrapper">
          <div className="weather-app">
            <form id="search-form" className="mb-3" onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-9">
                  <input
                    type="search"
                    placeholder="Enter a city"
                    className="form-control"
                    id="city-input"
                    autoComplete="off"
                    onChange={handleChange}
                  />
                </div>
                <div className="col-3">
                  <input
                    type="submit"
                    value="Search"
                    className="btn btn-primary w-100"
                  />
                </div>
              </div>
            </form>
            <WeatherInfo info={weatherData} />

            <div className="weather-forecast" id="forecast">
              <div className="row">
                <div className="col-2">
                  <div className="weather-forecast-date">Mon</div>
                  <img
                    src="http://shecodes-assets.s3.amazonaws.com/api/weather/icons/few-clouds-day.png"
                    alt="few-clouds-day"
                    width="60px"
                  />
                  <div className="weather-forecast-temperatures">
                    <span className="weather-forecast-maximum"> 17ยบ </span>
                    <span className="weather-forecast-minimum">13ยบ</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <small>
            <a href="https://github.com/MairaAl/weather-app">
              Open source code{" "}
            </a>
            by Maira
          </small>
        </div>
      </div>
    );
  } else {
    search();
    return "Loading...";
  }
}
