import axios from "axios";
import { useState } from "react";

export default function WeatherForecast(props) {
  let [loaded, setLoaded] = useState(false);
  let [forecast, setForecast] = useState("");
  function handleResponse(response) {
    console.log(response);
    setForecast(response.data.daily);
    setLoaded(true);
  }
  if (loaded) {
    console.log(forecast);
    return (
      <div className="weather-forecast" id="forecast">
        <div className="row">
          <div className="col-2">
            <div className="weather-forecast-date">Mon</div>
            <img
              src="http://shecodes-assets.s3.amazonaws.com/api/weather/icons/few-clouds-day.png"
              alt="few-clouds-day"
              width="60px"
            />
            <div className="weather-forecast-temperatures">
              <span className="weather-forecast-maximum">
                {" "}
                {Math.round(forecast[0].temperature.maximum)}{" "}
              </span>
              <span className="weather-forecast-minimum">
                {Math.round(forecast[0].temperature.minimum)}
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    let city = props.city;
    let apiKey = "b6dboa0a631739a3fcfd0t3ff674a04b";
    let apiUrl = `https://api.shecodes.io/weather/v1/forecast?query=${city}&key=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
  }
}
